package com.github.joba.pledger.controllerimport org.springframework.security.core.annotation.AuthenticationPrincipalimport org.springframework.security.oauth2.core.oidc.user.OidcUserimport org.springframework.web.bind.annotation.GetMappingimport org.springframework.web.bind.annotation.RequestMappingimport org.springframework.web.bind.annotation.RestController@RestController@RequestMapping("/api/test")class TestController {    @GetMapping("/oidc-principal")    fun getOidcUserPrincipal(@AuthenticationPrincipal principal: OidcUser): OidcUser {        println(principal.idToken?.givenName)        println(principal.idToken?.email)        println(principal.idToken?.subject)        return principal    }}